    db.collection.aggregate([
        {
            $addFields: {
                sortBy: { $ifNull: [ &quot;$parentId&quot;, &quot;$_id&quot; ] }
            }
        },
        {
            $sort: {
                sortBy: -1, parentId: 1
            }
        }
    ])

