                                        || to_char(extract(day from atable_ts) ) ) 
                            ,atable_tod  generated always as 
                                         ( to_char(extract(hour from atable_ts) ) || &#39;:&#39;   
                                        || to_char(extract(minute from atable_ts) ) || &#39;:&#39; 
                                        || to_char(extract(second from atable_ts) ) )
