        {
            if(!rb) rb = GetComponent&lt;Rigidbody2D&gt;();

            Scorescript.OnScoreChanged -= HandleScoreChange;
            Scorescript.OnScoreChanged += HandleScoreChange;
        }

        private void OnDestroy()
        {
            Scorescript.OnScoreChanged -= HandleScoreChange;
        }

        private void HandleScoreChange(int score)
        {
