    string fileName = &quot;C:\\Users\\Sameer\\Desktop\\Settings.dat&quot;;
    Windows.Storage.StorageFile file = await Windows.Storage.StorageFile.GetFileFromPathAsync(fileName);
      
    if(file != null)
