    private static final class PermutationIterator implements Iterator&lt;byte[]&gt; {
        private final int max;
        private final int n;
        private int current;

        public PermutationIterator(int n) {
            this.n = n;
            this.max = (int) Math.pow(2, n);
        }

        @Override
        public boolean hasNext() {
            return current &lt; max;
