        app.MapWhen(context =&gt; context.User.Identity.IsAuthenticated &amp;&amp; context.Request.Path.Value.StartsWith(&quot;/content&quot;), wp =&gt; 
        {
            wp.Map(new PathString(&quot;/content&quot;), wpMapper =&gt;
            {
                wpMapper.UseWordPress();
            });
            
        });

