    	if (sign){
    		number *= -1;
    	}
    	while (number){
    		string[index++] = (number % 10) + 48;
    		number/=10;
    		if (number == 0){
    			if (sign){
    				string[index++] = sign;
    			}
    			string[index++] = &#39;\n&#39;;
    			string[index--] = &#39;\0&#39;;
    			for (int i = 0, j = index - 1; i &lt; j; i++, j--){
