		&lt;a-entity  id=&quot;button2&quot; geometry=&quot;primitive: octahedron; radius: 0.2&quot; position=&quot;-4 0 -3&quot; class=&quot;clickable&quot; material=&quot;color: #4466BB&quot; button3d&gt;&lt;/a-entity&gt; 
