						SwingUtilities.invokeLater(() -&gt; {
							try {
								semaphore.acquire();
							} catch (InterruptedException e) {
								e.printStackTrace();
							}
							finally {
								semaphore.release();
								//Count down the latch to indicate that the thread terminated
