        let toggledEl = $(this).next(&#39;.my-toggled-element-class&#39;);

        // hide all elements that aren&#39;t the adjacent one
        $(&#39;.my-toggled-element&#39;).not(toggledEl).hide();

        // show just the adjacent one
        $(this).next(&#39;.my-toggled-element-class&#39;).toggle();
